import '../../css/Login.css';
import SignUp from './SignUp';
import ForgotPassword from './ForgotPassword';
import { useState } from 'react';
import axios from 'axios';


function Login(props) {


    const handleClick = () => {

    }



    return(
        <div className='login'>
        <ul>
            {
                this.state.persons
                .map(person => 
                    <li key={person.id}>{person.name}</li>
                )
            }
        </ul>

            <h2 id='siginId'>Sign-in</h2>
             <form className='loginContainer' >{/*onSubmit={handleSubmit}> */}

                <div className='formContainer'>

                    <div>
                        <label className='inputLabel' htmlFor='email'>Email <span className='asterick'>*</span></label><br></br>
                        <input className='inputArea' required
                                type='email' 
                                />
                    </div>
                    <div>
                        <label className='inputLabel' htmlFor='password'>Password <span className='asterick'>*</span></label><br></br>
                        <input className='inputArea' required
                                type="password"  
                                />
                    </div>
                    <div className='forgotPwdDiv'>
                        <button type="button" className='forgotpwd , buttonLink'
                        onClick={handleClick}> Forgot password? </button>
                    </div>
                    <div className='submitDiv'>
                        <button type="submit" className="submitButton"
                                >LOG IN</button>
                    </div>
                    <div className='labelAndSignUpDiv'>
                        Don't have an account?
                        <button type="button" className='signUp , buttonLink'>Sign up</button>
                    </div>
                    <div className='divider'><span>OR</span></div>
                    <div className='anonymousDiv'>
                        <button type="button" className='anonymousLink , buttonLink'>Continue as Anonymous</button>
                    </div>

                </div>
            </form>
        </div>
    );
}
export default Login;
