import '../../css/Login.css';
import SignUp from './SignUp';
import ForgotPassword from './ForgotPassword';

funhandleForgotPassword = ()=> {
    passwordForgotten(true);
}

function Login(props) {
    return(
        <div className="login">
                <form class='loginContainer'>
                    <h2 id='siginId'>Sign-in</h2>
                    <div className='formContainer'>
                        <div>
                            <label className='inputLabel'>Email <span className='asterick'>*</span></label>
                            <input type="email" required></input>
                        </div>
                        <div>
                            <label className='inputLabel'>Password <span className='asterick'>*</span></label>
                            <input type="password" required></input>
                        </div>
                            <button type="button" className='forgotpwd , buttonLink'
                            onClick = {handleForgotPassword}> Forgot Password? </button>
                            <div>
                            <button type="submit" className="submitButton">LOG IN</button>
                            </div>
                            <p>Don't have an account?</p>
                            <span><button type="button" className='signUp , buttonLink'>Sign up</button></span>
                    </div>
                </form>
        </div>
    );
}
export default Login;
