import '../../css/Login.css';
import SignUp from './SignUp';
import ForgotPassword from './ForgotPassword';
import { useState } from 'react';
import axios from 'axios';


function Login(props) {

    const handleForgotPasswordClick = () => {

    }
    const handleSignUpClick = () => {

    }

    const handleAnonymousClick = () => {

    }

    const handleChange = (event) => {
        this.setState({email : event.target.value});

    }

    const handleSubmit = () => {
        const email = {
            email: this.state.e
          };
        axios.post('https://jsonplaceholder.typicode.com/posts', {email})
        .then(response => {
            console.log(response)
        })
    }


    return(
        <div className='login'>

            <h2 id='siginId'>Sign-in</h2>

             <form className='loginContainer' onSubmit={handleSubmit}> 

                <div className='formContainer'>

                    <div>
                        <label className='inputLabel' htmlFor='email'>Email <span className='asterick'>*</span></label><br></br>
                        <input required className='inputArea' type='email' 
                                onChange={this.handleChange}/>
                    </div>

                    <div>
                        <label className='inputLabel' htmlFor='password'>Password <span className='asterick'>*</span></label><br></br>
                        <input required className='inputArea' type="password" 
                                onChange={handleChange}/>
                    </div>

                    <div className='forgotPwdDiv'>
                        <button type="button" className='forgotpwd , buttonLink'
                        onClick={handleForgotPasswordClick}> Forgot password? </button>
                    </div>

                    <div className='submitDiv'>
                        <button type="submit" className="submitButton"
                                onClick={handleSubmit}
                                >LOG IN</button>
                    </div>

                    <div className='labelAndSignUpDiv'>
                        Don't have an account?
                        <button type="button" className='signUp , buttonLink'
                                onClick={handleSignUpClick}>Sign up</button>
                    </div>

                    <div className='divider'><span>OR</span></div>

                    <div className='anonymousDiv'>
                        <button type="button" className='anonymousLink , buttonLink'
                                onClick={handleAnonymousClick}>Continue as Anonymous</button>
                    </div>

                </div>

            </form>

        </div>
    );
}
export default Login;
