import '../../css/Main.css';
import InitialImage from '../../images/InitialImage.jpg';
import ForgotPassword from './ForgotPassword';
import SignUp from './SignUp';
import FormContainer from './FormContainer';
import Login from './Login';
import { useState } from 'react';

function Main() {

    const[isForgotPassword, setForgotPassword] = useState(false);
    const[isSignUp, setSignUp] = useState(false);

    return(
        <div className="main">

            <div className="mainLeftPanel">
                <img className="initialImage" src={InitialImage} alt="InitalLoad"/>
            </div>

            <div className="mainRightPanel">
                <div className="emptySection"></div>
                <div className="formContainer">
                    {() => {
                        if(isForgotPassword) { <ForgotPassword/> }
                        else if (isSignUp) { <SignUp/> }
                        else { <Login/> }
                    }}()
                </div>
                <div className="emptySection"></div>
            </div>

        </div>
    );
}
export default Main;
